PRAGMA foreign_keys=OFF;
BEGIN TRANSACTION;
CREATE TABLE IF NOT EXISTS "migrations" ("id" integer primary key autoincrement not null, "migration" varchar not null, "batch" integer not null);
CREATE TABLE IF NOT EXISTS "users" ("id" integer primary key autoincrement not null, "name" varchar not null, "email" varchar not null, "role" varchar, "phone" varchar, "email_verified_at" datetime, "password" varchar not null, "remember_token" varchar, "current_team_id" integer, "profile_photo_path" varchar, "created_at" datetime, "updated_at" datetime, "two_factor_secret" text, "two_factor_recovery_codes" text, "two_factor_confirmed_at" datetime);
CREATE TABLE IF NOT EXISTS "password_reset_tokens" ("email" varchar not null, "token" varchar not null, "created_at" datetime, primary key ("email"));
CREATE TABLE IF NOT EXISTS "sessions" ("id" varchar not null, "user_id" integer, "ip_address" varchar, "user_agent" text, "payload" text not null, "last_activity" integer not null, primary key ("id"));
CREATE TABLE IF NOT EXISTS "cache" ("key" varchar not null, "value" text not null, "expiration" integer not null, primary key ("key"));
CREATE TABLE IF NOT EXISTS "cache_locks" ("key" varchar not null, "owner" varchar not null, "expiration" integer not null, primary key ("key"));
CREATE TABLE IF NOT EXISTS "jobs" ("id" integer primary key autoincrement not null, "queue" varchar not null, "payload" text not null, "attempts" integer not null, "reserved_at" integer, "available_at" integer not null, "created_at" integer not null);
CREATE TABLE IF NOT EXISTS "job_batches" ("id" varchar not null, "name" varchar not null, "total_jobs" integer not null, "pending_jobs" integer not null, "failed_jobs" integer not null, "failed_job_ids" text not null, "options" text, "cancelled_at" integer, "created_at" integer not null, "finished_at" integer, primary key ("id"));
CREATE TABLE IF NOT EXISTS "failed_jobs" ("id" integer primary key autoincrement not null, "uuid" varchar not null, "connection" text not null, "queue" text not null, "payload" text not null, "exception" text not null, "failed_at" datetime not null default CURRENT_TIMESTAMP);
CREATE TABLE IF NOT EXISTS "personal_access_tokens" ("id" integer primary key autoincrement not null, "tokenable_type" varchar not null, "tokenable_id" integer not null, "name" varchar not null, "token" varchar not null, "abilities" text, "last_used_at" datetime, "expires_at" datetime, "created_at" datetime, "updated_at" datetime);
CREATE TABLE IF NOT EXISTS "staff" ("id" integer primary key autoincrement not null, "status" varchar not null default 'active', "user_id" integer not null, "pharmacy_id" integer not null, "created_at" datetime, "updated_at" datetime, foreign key("user_id") references "users"("id") on delete cascade, foreign key("pharmacy_id") references "pharmacies"("id") on delete cascade);
CREATE TABLE IF NOT EXISTS "sales" ("id" integer primary key autoincrement not null, "pharmacy_id" integer not null, "item_id" integer not null, "staff_id" integer not null, "stock_id" integer not null, "quantity" integer not null, "total_price" numeric not null, "date" datetime not null, "created_at" datetime, "updated_at" datetime, foreign key("pharmacy_id") references "pharmacies"("id") on delete cascade, foreign key("item_id") references "items"("id") on delete cascade, foreign key("staff_id") references "users"("id") on delete cascade, foreign key("stock_id") references "stocks"("id") on delete cascade);
CREATE TABLE IF NOT EXISTS "stocks" ("id" integer primary key autoincrement not null, "in_date" datetime not null, "quantity" integer not null, "batch_number" integer not null, "supplier" varchar not null, "remain_Quantity" integer not null, "low_stock_percentage" integer not null, "expire_date" datetime not null, "buying_price" numeric not null, "selling_price" numeric not null, "staff_id" integer not null, "item_id" integer not null, "pharmacy_id" integer not null, "created_at" datetime, "updated_at" datetime, foreign key("staff_id") references "users"("id") on delete cascade, foreign key("item_id") references "items"("id") on delete cascade, foreign key("pharmacy_id") references "pharmacies"("id") on delete cascade);
CREATE TABLE IF NOT EXISTS "items" ("id" integer primary key autoincrement not null, "name" varchar not null, "category_id" integer not null, "pharmacy_id" integer not null, "created_at" datetime, "updated_at" datetime, foreign key("category_id") references "categories"("id") on delete cascade, foreign key("pharmacy_id") references "pharmacies"("id") on delete cascade);
CREATE TABLE IF NOT EXISTS "categories" ("id" integer primary key autoincrement not null, "name" varchar not null, "description" text, "pharmacy_id" integer not null, "created_at" datetime, "updated_at" datetime, foreign key("pharmacy_id") references "pharmacies"("id") on delete cascade);
CREATE TABLE IF NOT EXISTS "packages" ("id" integer primary key autoincrement not null, "name" varchar not null, "price" numeric not null, "duration" varchar not null, "status" tinyint(1) not null default '1', "created_at" datetime, "updated_at" datetime, "number_of_pharmacies" integer, "number_of_pharmacists" integer, "number_of_medicines" integer, "in_app_notification" tinyint(1) not null default '0', "email_notification" tinyint(1) not null default '0', "sms_notifications" tinyint(1) not null default '0', "online_support" tinyint(1) not null default '0', "number_of_owner_accounts" integer, "number_of_admin_accounts" integer, "reports" tinyint(1) not null default '0', "stock_transfer" tinyint(1) not null default '0', "stock_management" tinyint(1) not null default '0', "staff_management" tinyint(1) not null default '0', "receipts" tinyint(1) not null default '0', "analytics" tinyint(1) not null default '0', "whatsapp_chats" tinyint(1) not null default '0');
CREATE TABLE IF NOT EXISTS "medicines" ("id" integer primary key autoincrement not null, "brand_name" text not null, "generic_name" text, "category" text, "class" text, "dosage_form" text, "description" text, "status" text not null default 'approved', "strength" text, "manufacturer" text, "manufacturing_country" text, "created_at" datetime, "updated_at" datetime);
CREATE TABLE IF NOT EXISTS "notifications" ("id" varchar not null, "type" varchar not null, "notifiable_type" varchar not null, "notifiable_id" integer not null, "data" text not null, "read_at" datetime, "created_at" datetime, "updated_at" datetime, primary key ("id"));
CREATE TABLE IF NOT EXISTS "contracts" ("id" integer primary key autoincrement not null, "owner_id" integer not null, "package_id" integer not null, "start_date" date not null, "end_date" date not null, "status" varchar check ("status" in ('active', 'inactive', 'graced')) not null, "grace_end_date" date, "payment_status" varchar check ("payment_status" in ('payed', 'unpayed', 'pending')) not null, "is_current_contract" tinyint(1) not null default '0', "created_at" datetime, "updated_at" datetime, foreign key("owner_id") references "users"("id") on delete cascade, foreign key("package_id") references "packages"("id") on delete cascade);
CREATE TABLE IF NOT EXISTS "printer_settings" ("id" integer primary key autoincrement not null, "name" varchar not null, "ip_address" varchar not null, "computer_name" varchar not null, "port" varchar, "use_printer" tinyint(1) not null default '0', "pharmacy_id" integer not null, "created_at" datetime, "updated_at" datetime, foreign key("pharmacy_id") references "pharmacies"("id") on delete cascade);
CREATE TABLE IF NOT EXISTS "sale_notes" ("id" integer primary key autoincrement not null, "name" varchar not null, "quantity" varchar not null, "unit_price" varchar not null, "status" varchar check ("status" in ('promoted', 'Unpromoted', 'rejected')) not null default 'Unpromoted', "description" text, "pharmacy_id" integer not null, "staff_id" integer not null, "created_at" datetime, "updated_at" datetime, foreign key("pharmacy_id") references "pharmacies"("id") on delete cascade, foreign key("staff_id") references "users"("id") on delete cascade);
CREATE TABLE IF NOT EXISTS "pharmacies" ("id" integer primary key autoincrement not null, "name" varchar not null, "location" text, "status" text, "package_id" integer not null, "owner_id" integer not null, "created_at" datetime, "updated_at" datetime, "agent_id" integer not null default '1', foreign key("owner_id") references users("id") on delete cascade on update no action, foreign key("package_id") references packages("id") on delete cascade on update no action, foreign key("agent_id") references "users"("id"));
CREATE TABLE IF NOT EXISTS "messages" ("id" integer primary key autoincrement not null, "sender_id" integer not null, "content" text not null, "status" varchar check ("status" in ('unread', 'read', 'deleted')) not null default 'unread', "parent_message_id" integer, "created_at" datetime, "updated_at" datetime, "subject" varchar, "attachment" varchar, "message_type" varchar check ("message_type" in ('text', 'image', 'file', 'voice')) not null default 'text', "is_urgent" tinyint(1) not null default '0', "conversation_id" integer not null, "deleted_at" datetime, foreign key("parent_message_id") references "messages"("id") on delete no action, foreign key("sender_id") references "users"("id") on delete cascade, foreign key("conversation_id") references "conversations"("id") on delete cascade);
CREATE TABLE IF NOT EXISTS "conversations" ("id" integer primary key autoincrement not null, "creator_id" integer not null, "title" text not null, "description" text, "status" varchar check ("status" in ('open', 'closed', 'solved', 'unsolved')) not null default 'open', "created_at" datetime, "updated_at" datetime, foreign key("creator_id") references "users"("id") on delete cascade);
CREATE TABLE IF NOT EXISTS "conversations_users" ("id" integer primary key autoincrement not null, "conversation_id" integer not null, "user_id" integer not null, "created_at" datetime, "updated_at" datetime, foreign key("conversation_id") references "conversations"("id") on delete cascade, foreign key("user_id") references "users"("id") on delete cascade);
CREATE TABLE IF NOT EXISTS "message_user_read" ("id" integer primary key autoincrement not null, "message_id" integer not null, "user_id" integer not null, "read_at" datetime, "created_at" datetime, "updated_at" datetime, foreign key("message_id") references "messages"("id") on delete cascade, foreign key("user_id") references "users"("id") on delete cascade);
CREATE TABLE IF NOT EXISTS "sales_returns" ("id" integer primary key autoincrement not null, "sale_id" integer not null, "pharmacy_id" integer not null, "staff_id" integer not null, "quantity" integer not null, "refund_amount" numeric, "reason" varchar, "return_status" varchar check ("return_status" in ('pending', 'approved', 'rejected')) not null default 'pending', "approved_by" integer, "date" datetime not null, "created_at" datetime, "updated_at" datetime, foreign key("sale_id") references "sales"("id") on delete cascade, foreign key("pharmacy_id") references "pharmacies"("id") on delete cascade, foreign key("staff_id") references "users"("id") on delete cascade, foreign key("approved_by") references "users"("id") on delete set null);
CREATE TABLE IF NOT EXISTS "agents" ("id" integer primary key autoincrement not null, "user_id" integer not null, "country" varchar, "NIN" varchar, "document_attachment_1_name" varchar, "document_attachment_2_name" varchar, "document_attachment_3_name" varchar, "document_attachment_1" varchar, "document_attachment_2" varchar, "document_attachment_3" varchar, "signed_agreement_form" varchar, "agent_code" varchar, "request_date" date, "registration_status" varchar check ("registration_status" in ('step_1', 'step_2', 'step_3', 'complete', 'incomplete')) not null default 'incomplete', "status" varchar check ("status" in ('verified', 'unverified')) not null default 'unverified', "verified_by" integer, "verified_date" date, "address" text, "created_at" datetime, "updated_at" datetime, foreign key("user_id") references "users"("id") on delete cascade, foreign key("verified_by") references "users"("id") on delete cascade);
CREATE TABLE IF NOT EXISTS "audits" ("id" integer primary key autoincrement not null, "user_type" varchar, "user_id" integer, "event" varchar not null, "auditable_type" varchar not null, "auditable_id" integer not null, "old_values" text, "new_values" text, "url" text, "ip_address" varchar, "user_agent" varchar, "tags" varchar, "created_at" datetime, "updated_at" datetime);
CREATE TABLE IF NOT EXISTS "stock_transfers" ("id" integer primary key autoincrement not null, "stock_id" integer not null, "from_pharmacy_id" integer not null, "to_pharmacy_id" integer, "to_pharmacy_name" varchar, "to_pharmacy_tin" varchar, "notes" varchar, "quantity" integer not null, "transfer_date" datetime not null default CURRENT_TIMESTAMP, "status" varchar check ("status" in ('pending', 'completed')) not null default 'pending', "transferred_by" integer not null, "received_by" integer, "created_at" datetime, "updated_at" datetime, foreign key("stock_id") references "stocks"("id") on delete cascade, foreign key("from_pharmacy_id") references "pharmacies"("id") on delete cascade, foreign key("to_pharmacy_id") references "pharmacies"("id") on delete cascade, foreign key("transferred_by") references "users"("id") on delete cascade, foreign key("received_by") references "users"("id") on delete cascade);
DELETE FROM sqlite_sequence;
CREATE UNIQUE INDEX "users_email_unique" on "users" ("email");
CREATE INDEX "sessions_user_id_index" on "sessions" ("user_id");
CREATE INDEX "sessions_last_activity_index" on "sessions" ("last_activity");
CREATE INDEX "jobs_queue_index" on "jobs" ("queue");
CREATE UNIQUE INDEX "failed_jobs_uuid_unique" on "failed_jobs" ("uuid");
CREATE INDEX "personal_access_tokens_tokenable_type_tokenable_id_index" on "personal_access_tokens" ("tokenable_type", "tokenable_id");
CREATE UNIQUE INDEX "personal_access_tokens_token_unique" on "personal_access_tokens" ("token");
CREATE INDEX "notifications_notifiable_type_notifiable_id_index" on "notifications" ("notifiable_type", "notifiable_id");
CREATE UNIQUE INDEX "agents_user_id_unique" on "agents" ("user_id");
CREATE UNIQUE INDEX "agents_nin_unique" on "agents" ("NIN");
CREATE UNIQUE INDEX "agents_agent_code_unique" on "agents" ("agent_code");
CREATE INDEX "audits_auditable_type_auditable_id_index" on "audits" ("auditable_type", "auditable_id");
CREATE INDEX "audits_user_id_user_type_index" on "audits" ("user_id", "user_type");
COMMIT;
